import Card from '../Card/Card';
import style from "../Cards/Cards.module.css"

const Cards = ({characters, onClose}) => {
   return ( 
      <div className={style.estilos}>
         {
            characters.map(({id, name, species, gender, image, orig}) =>{ //se puede destrucuturar el nombre de cada uno de los parametros que estamos solicitando (no usar char."nonbre del parametro")
               return (
                  <Card 
                  onClose={onClose}
                  key={id}
                  id={id}
                  name= {char.name}
                  species={char.species}
                  gender={char.gender}
                  image={char.image}
                  origin={char.origin.name}
                  status={char.status}
                  />
               )
            })
         }
      </div>
   )
}

export default Cards;
